(function($){$.fn.equalBreak=function(options){var settings=$.extend({lines:2},options);return this.each(function(){var $el=$(this),text=$el.html(),words=text.split(" "),breakPoints=parseInt((text.length/settings.lines),10),breakIncr=breakPoints,letterCount=0,result="";for(i=0;i<words.length;i++){if(letterCount>=breakPoints){result+="<br>";breakPoints+=breakIncr}result+=" "+words[i];letterCount+=words[i].length+1}$el.html(result)})}}(jQuery));